import React from "react";
import M from "materialize-css";
import { Button } from "react-materialize";
import SimplePortal from "./components/SimplePortal";

class Login extends React.Component {
  componentDidMount() {
    // Auto initialize all the things!
    M.AutoInit();
    function check_login_state(){
      var sender_object = {
        xhr: new XMLHttpRequest(),
        send:function(){
          this.xhr.open("POST","http://localhost:8080/api/login",true);
          this.xhr.onreadystatechange = this.callback;
          this.xhr.withCredentials = true;
          this.xhr.send();
        },
        callback:function(){
          if (this.readyState == 4){
            if (this.status == 202){
              alert("You are already logged in");
              window.location.href = "/home";
            }
            if (this.status == 403){
              alert("User not found");
              document.cookie = "username=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              document.cookie = "loginstring=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              window.location.href="/login";
            }
            if(this.status == 401){
              alert("incorrect password");
              document.cookie = "username=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              document.cookie = "loginstring=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              window.location.href="/login";
            }
            if (this.status == 204){
              //not yet logged in. no problem.
              document.cookie = "username=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              document.cookie = "loginstring=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
              console.log("Login Again");
            }
          }
        }
      }
      sender_object.send();
    }
    check_login_state();
  }
  render() {
    return (
      <>
        <main className="col s2">
            <div className="row">
            <div className="col s12 m4 offset-m2 card center">
            <button className="btn btn-small blue waves-effect" onClick={attempt_login}>
                Compose Mail
              </button>
            </div>
            </div>
          <div className="row">
            <div className="col s12 m4 offset-m0 card center">
              <p></p>
            </div>
            <div className="col s12 m4 offset-m0 card center">
              <p>Home Page 1</p>
            </div>
            <div className="col s12 m4 offset-m0 card center">
              <p>Home Page 2</p>
            </div>
          </div>


        </main>
      </>
    );
  }
}

export default Login;
