import React from "react";

class Modal extends React.Component {
  onClose = e => {
    this.props.onClose && this.props.onClose(e);
  };
  render() {
    if(!this.props.compose){
      return null;
  }
return( <div>{this.props.children}
  <button onClick={e => {
              this.onClose(e);
            }}>
              Close
            </button>
</div>);
  }
}

export default Modal;
