import React from "react";
import "materialize-css";
import M from "materialize-css";
import { DatePicker } from "react-materialize";
import { NavLink } from "react-router-dom";
import Particles from 'react-particles-js';

class Signup extends React.Component {
  componentDidMount() {
    // Auto initialize all the things!
    M.AutoInit();
    function check_login_state() {
      var sender_object = {
        xhr: new XMLHttpRequest(),
        send: function () {
          const token = localStorage.getItem('token');
          //alert("Sending request");
          this.xhr.open("GET", "http://localhost:8080/users/checkLogged", true);
          this.xhr.onreadystatechange = this.callback;
          this.xhr.setRequestHeader('Authorization', token);
          this.xhr.send();
        },
        callback: function () {
          if (this.readyState == 4) {
            if (this.status == 200) {
              //alert("Not logged in");
              //alert(this.status);
              window.location = '/home';
            }
          }
        }
      }
      sender_object.send();
    }
    check_login_state();
  }
  render() {
    function attempt_signup() {
      var sender_object = {
        xhr: new XMLHttpRequest(),
        send: function () {
          let data = {
            fname: document.getElementById("firstname").value,
            lname: document.getElementById("lastname").value,
            user: document.getElementById("email").value,
            password: document.getElementById("password").value,
            dob: document.getElementById("dob").value,
            no: document.getElementById("phoneno").value
          }
          this.xhr.open("POST", "http://localhost:8080/users/signup", true);
          this.xhr.setRequestHeader("Content-Type", "application/json;charset=UTF-8");
          this.xhr.onreadystatechange = this.callback;
          console.log("Sending data");
          console.log(data);
          this.xhr.send(JSON.stringify(data));
        },
        callback: function () {
          if (this.readyState === 4 && this.status === 201) {
            alert("Successful Signup");
            window.location = '/login';
          }
        }
      }
      sender_object.send()
    }

    function checkUser() {
      console.log("Checking user");
      var sender_object = {
        xhr: new XMLHttpRequest(),
        send: function () {
          this.xhr.open("GET", "http://localhost:8080/users/check?user=" + document.getElementById("email").value, true);
          this.xhr.onreadystatechange = this.callback;
          this.xhr.send();
        },
        callback: function () {
          if (this.readyState === 4) {
            const button = document.getElementById("signup");
            if (this.status == 404) {
              console.log("404");
              button.removeAttribute("disabled");
            }
            if (this.status == 200) {
              console.log("200");
              alert("User Already Exists");
              button.disabled = "true";
            }
            if (this.status == 500) {
              console.log("500");
              alert("server error");
            }
          }
        }
      }
      sender_object.send()
    }

    const particleStyle = {
      width: "100%",
      height: "100%",
      position: "fixed",
      "z-index": "-10",
      top: "0",
      left: "0",
      "background-color": "#2c2e43"
    }

    const divStyle = {
      "background-color": "#ededf0"
    }
    return (
      <>
        <main>
          <Particles style={particleStyle}
            params={{
              "particles": {
                "number": {
                  "value": 6,
                  "density": {
                    "enable": true,
                    "value_area": 800
                  }
                },
                "color": {
                  "value": "#1b1e34"
                },
                "shape": {
                  "type": "polygon",
                  "stroke": {
                    "width": 0,
                    "color": "#000"
                  },
                  "polygon": {
                    "nb_sides": 6
                  },
                  "image": {
                    "src": "img/github.svg",
                    "width": 100,
                    "height": 100
                  }
                },
                "opacity": {
                  "value": 0.3,
                  "random": true,
                  "anim": {
                    "enable": false,
                    "speed": 1,
                    "opacity_min": 0.1,
                    "sync": false
                  }
                },
                "size": {
                  "value": 160,
                  "random": false,
                  "anim": {
                    "enable": true,
                    "speed": 10,
                    "size_min": 40,
                    "sync": false
                  }
                },
                "line_linked": {
                  "enable": false,
                  "distance": 200,
                  "color": "#ffffff",
                  "opacity": 1,
                  "width": 2
                },
                "move": {
                  "enable": true,
                  "speed": 8,
                  "direction": "none",
                  "random": false,
                  "straight": false,
                  "out_mode": "out",
                  "bounce": false,
                  "attract": {
                    "enable": false,
                    "rotateX": 600,
                    "rotateY": 1200
                  }
                }
              },
              "interactivity": {
                "detect_on": "canvas",
                "events": {
                  "onhover": {
                    "enable": false,
                    "mode": "grab"
                  },
                  "onclick": {
                    "enable": false,
                    "mode": "push"
                  },
                  "resize": true
                },
                "modes": {
                  "grab": {
                    "distance": 400,
                    "line_linked": {
                      "opacity": 1
                    }
                  },
                  "bubble": {
                    "distance": 400,
                    "size": 40,
                    "duration": 2,
                    "opacity": 8,
                    "speed": 3
                  },
                  "repulse": {
                    "distance": 200,
                    "duration": 0.4
                  },
                  "push": {
                    "particles_nb": 4
                  },
                  "remove": {
                    "particles_nb": 2
                  }
                }
              },
              "retina_detect": true
            }}
          />
          <div className="row s12 m3 offset-m3" style={{marginTop: "1%"}}>
            <div className="col s12 m3 offset-m3 card center" style={divStyle}>
              <h5>Sign-up</h5>
            </div>
            <div className="col s12 m3 offset-m0 card center" style={divStyle}>
              <h5>
                <NavLink to="/login" exact>
                  Login
                  </NavLink>
              </h5>
            </div>
          </div>
          <div className="row">
            <div className="col s12 m6 offset-m3 card center" style={divStyle}>

              <div className="input-field col s12">
                <input className="validate" type="text" name="email" id="email" onBlur={checkUser} required />
                <label for="email">Username</label>
              </div>

              <div className="input-field col s12">
                <input className="validate" type="text" name="firstname" id="firstname" required />
                <label for="firstname">First Name</label>
              </div>
              <div className="input-field col s12">
                <input className="validate" type="text" name="lastname" id="lastname" required />
                <label for="lastname">Last Name</label>
              </div>


              <div className="input-field col s12">
                <input className="validate" type="password" name="password" id="password" required />
                <label for="password">Password</label>
              </div>

              <div className="input-field col s12">
                <input className="validate" type="tel" name="phoneno" id="phoneno" maxLength="10" minLength="10" required />
                <label for="phoneno">Phone No</label>
              </div>

              <div className="input-field col s12">
                <DatePicker name="dob" id="dob" required />
                <label for="dob">DOB</label>
              </div>



              <button id="signup" className="btn btn-small blue waves-effect" onClick={attempt_signup}>
                SignUp
              </button>
            </div>
          </div>
        </main>
      </>
    );
  }
}

export default Signup;
