import React from "react";
import Particles from 'react-particles-js';

class Intro extends React.Component {
    render() {
        const particleStyle = {
            width: "100%",
            height: "100%",
            position: "fixed",
            "z-index": "-10",
            top: "0",
            left: "0",
            "background-color": "#2c2e43"
        }
        const divStyle = {
            "background-color": "#ededf0",
            display: "inline-block",
            "border-radius": "2%",
            "position": "absolute",
            "top": "50%",
            left: "50%",
            transform: "translate(-50%,-50%)",
            "box-shadow": "5px 5px"
        }
        return (
            <main>
                <Particles style={particleStyle}
                    params={{
                        "particles": {
                            "number": {
                                "value": 6,
                                "density": {
                                    "enable": true,
                                    "value_area": 800
                                }
                            },
                            "color": {
                                "value": "#1b1e34"
                            },
                            "shape": {
                                "type": "polygon",
                                "stroke": {
                                    "width": 0,
                                    "color": "#000"
                                },
                                "polygon": {
                                    "nb_sides": 6
                                },
                                "image": {
                                    "src": "img/github.svg",
                                    "width": 100,
                                    "height": 100
                                }
                            },
                            "opacity": {
                                "value": 0.3,
                                "random": true,
                                "anim": {
                                    "enable": false,
                                    "speed": 1,
                                    "opacity_min": 0.1,
                                    "sync": false
                                }
                            },
                            "size": {
                                "value": 160,
                                "random": false,
                                "anim": {
                                    "enable": true,
                                    "speed": 10,
                                    "size_min": 40,
                                    "sync": false
                                }
                            },
                            "line_linked": {
                                "enable": false,
                                "distance": 200,
                                "color": "#ffffff",
                                "opacity": 1,
                                "width": 2
                            },
                            "move": {
                                "enable": true,
                                "speed": 8,
                                "direction": "none",
                                "random": false,
                                "straight": false,
                                "out_mode": "out",
                                "bounce": false,
                                "attract": {
                                    "enable": false,
                                    "rotateX": 600,
                                    "rotateY": 1200
                                }
                            }
                        },
                        "interactivity": {
                            "detect_on": "canvas",
                            "events": {
                                "onhover": {
                                    "enable": false,
                                    "mode": "grab"
                                },
                                "onclick": {
                                    "enable": false,
                                    "mode": "push"
                                },
                                "resize": true
                            },
                            "modes": {
                                "grab": {
                                    "distance": 400,
                                    "line_linked": {
                                        "opacity": 1
                                    }
                                },
                                "bubble": {
                                    "distance": 400,
                                    "size": 40,
                                    "duration": 2,
                                    "opacity": 8,
                                    "speed": 3
                                },
                                "repulse": {
                                    "distance": 200,
                                    "duration": 0.4
                                },
                                "push": {
                                    "particles_nb": 4
                                },
                                "remove": {
                                    "particles_nb": 2
                                }
                            }
                        },
                        "retina_detect": true
                    }}
                />
                <div className="waves-effect waves-light" style={divStyle}>
                    <h5 className="center"><b>Introduction Page</b></h5>
                    <pre className="center"> A basic Intra Mailing system using React, Node, MongoDB, Express </pre>
                </div>
            </main>
        )
    };
}

export default Intro;
